public boolean processRow(StepMetaInterface smi, StepDataInterface sdi) throws KettleException
{
    // First, get a row from the default input hop
	Object[] r = getRow();

    // If the row object is null, we are done processing.
	if (r == null) {
		setOutputDone();
		return false;
	}

    // If the global "first" flag is true, perform some initialization that can only happen
    // once we have read the first row of input data
    if (first) {
        first = false;

		fifo = new LinkedList();
		outputRowSize = data.outputRowMeta.size();
		valueIndex = getInputRowMeta().indexOfValue("actualMeterLoad(kW)");		
	}

	Long value = getInputRowMeta().getInteger(r, valueIndex);
	fifo.addLast(value);
	if (fifo.size()>90) {
      fifo.removeFirst();
	}

	double avg = 0;
	ListIterator it = fifo.listIterator(0);
    while (it.hasNext()) {
      avg+=((Long)it.next()).longValue();
    }

/*
	for (int i=0;i<fifo.size();i++) {
    	avg+=((Long)fifo.get(i)).longValue();
    }
*/
	avg/=fifo.size();

    // It is always safest to call createOutputRow() to ensure that your output row's Object[] is large
    // enough to handle any new fields you are creating in this step.
	//
    r = createOutputRow(r, outputRowSize);
    r[getInputRowMeta().size()] = Double.valueOf(avg);

    // putRow will send the row on to the default output hop.
    putRow(data.outputRowMeta, r);

	return true;
}

