function login () 
{
	var serviceTicket = null;
	// The login method simulates the posting of the CAS login form. The login form contains a unique identifier
	// or "LT" that is only valid for 90s. The method getLTFromLoginForm requests the login form from the cAS
	// and extracts the LT that we need.  Note that the LT is _service specific_ : We need to use an identical
	// serviceUrl when retrieving and posting the login form.
	lt = getLTFromLoginForm ();
    writeToLog(TAG+"LT " + lt);
	if (lt == null)
	{
		writeToLog(TAG+"Cannot retrieve LT from CAS. Aborting authentication for '" + username + "'");
		//throw new CasProtocolException ("Cannot retrieve LT from CAS. Aborting authentication for '" + username + "'");
	}
	else
	{
		// Yes, it is necessary to include the serviceUrl as part of the query string. The URL must be
		// identical to that used to get the LT.
		writeToLog(TAG+"POST " + loginUrl);
		var httpPost = new Packages.org.apache.commons.httpclient.methods.PostMethod(loginUrl);
		try
		{
            var params = [];
        	params.push(new org.apache.commons.httpclient.NameValuePair("_eventId", "submit"));
			params.push(new org.apache.commons.httpclient.NameValuePair("username", "keswa@viridityenergy.com"));
			params.push(new org.apache.commons.httpclient.NameValuePair("gateway", "true"));
			params.push(new org.apache.commons.httpclient.NameValuePair("password", "password"));
			params.push(new org.apache.commons.httpclient.NameValuePair("lt", lt));
			//params.add(new org.apache.commons.httpclient.NameValuePair("_eventId", "submit"));	
			//params.add(new org.apache.commons.httpclient.NameValuePair("username", username));
            //params.add(new org.apache.commons.httpclient.NameValuePair("password", password));
            //params.add(new org.apache.commons.httpclient.NameValuePair("gateway", "true"));
            //params.add(new org.apache.commons.httpclient.NameValuePair("lt", lt));
            httpPost.addParameters(params);
			var httpClient = new Packages.org.apache.commons.httpclient.HttpClient();
			httpClient.setConnectionTimeout(0);
			httpClient.setTimeout(0);
			http_status_code = httpClient.executeMethod(httpPost);
			writeToLog(TAG+"POST RESPONSE STATUS=" + http_status_code);

    		var stringBuilder_ = new java.lang.StringBuilder();
    		var isr_ = new Packages.java.io.InputStreamReader(httpPost.getResponseBodyAsStream());
    		var rd_ = new java.io.BufferedReader(isr_);
    		while ((line = rd_.readLine()) != null) {
     			stringBuilder_.append(line);
    		}
    		afterLoginResp = stringBuilder_.toString();

          	var hdrs_ = httpPost.getResponseHeaders();
			for (var i = hdrs_.length; i--;) {
				writeToLog(TAG+"HEADER=" + hdrs_[i].getValue());
			}

	//var json = stringBuilder.toString();

			//TODO It would seem that when the client is already authenticated, the CAS server 
			// redirects transparently to the service URL!
			// Success if CAS replies with a 302 HTTP status code and a Location header
			// We assume that if a valid ticket is provided in the Location header, that it is also a 302 HTTP STATUS
			var header = httpPost.getResponseHeader("Location");
            writeToLog(TAG+"POST LOCATION HEADER=" + header);
			if (header != null) 
				serviceTicket = extractServiceTicket (header.getValue());


			if (serviceTicket == null)
			{
				writeToLog(TAG+"Authentication to service '" + serviceUrl + "' unsuccessul for username '" + username + "'.");
				//throw new CasAuthenticationException ("Authentication to service '" + serviceUrl + "' unsuccessul for username '" + username + "'.");
			}
			else
				writeToLog(TAG+"Authentication to service '" + serviceUrl + "' successul for username '" + username + "'.");
		} 
		catch (e) 
		{
			writeToLog(TAG+"IOException trying to login : " + e.getMessage());
			//throw new CasProtocolException ("IOException trying to login : " + e.getMessage());
		} 
		return serviceTicket;
	}
}

function extractServiceTicket (data)
{
	var serviceTicket = null;
	var start = data.indexOf(CAS_TICKET_BEGIN);
	if (start > 0)
	{
		start += CAS_TICKET_BEGIN.length ();
		serviceTicket = data.substring (start);
	}
	return serviceTicket;
}

function extractLt (dataStream)
{
  var reader = java.io.BufferedReader(dataStream);
  var token = null;
  var stringBuilder = new java.lang.StringBuilder();
  try 
  {
    while (token == null && (line = reader.readLine()) != null)
    {
      var start = line.indexOf (CAS_LT_BEGIN);
      if (start >= 0)
      {
        start += CAS_LT_BEGIN.length;
        var end = line.indexOf("\"", start);
        token = line.substring (start, end);
      }
	  stringBuilder.append(line);
    }
  } 
  catch (e) 
  {
    writeToLog(TAG+': '+e);
  }
  return token;//token;//token;
}


function _downloadReports(location) {
	var rows = [];
	while (location) {
		// Request link list
		var method = _apiRequest(location, {}, 'GET');
		var json = JSON.parse(method.getResponseBodyAsString());
		// Check if we got an valid link list
		if (!json || !json.total) {
			return rows;
		}
		rows = rows.concat(json.rows);
		location = null;
		for (var i = json.links.length; i--;) {
			if (json.links[i].rel == 'next') {
				location = json.links[i].href;
				break;
			}
		}
	}
	return rows;
};

function _statusRequest(location, limit) {
	// Maximum 50 tries to get complete status response
	for (var i = 50; i--;) {
		// Request report status
		var statusMethod = _apiRequest(location, {'limit': limit || 100}, 'GET');
		return statusMethod;
		var json = JSON.parse(statusMethod.getResponseBodyAsString());
		// Check if the response is correct and the report is completed
		if (!!json && (json.completed || json.error)) {
			// Return success xhr
			return statusMethod;
		}
	}
	return null;
};
