select meter_event_site_fk as site_key, meter_event_lc_date_fk as lc_date_max, meter_event_lc_time_fk as lc_time_max
from (
     select meter_event_site_fk, meter_event_lc_date_fk, meter_event_lc_time_fk
          , row_number() over (partition by meter_event_site_fk
                               order by meter_event_lc_date_fk desc, meter_event_lc_time_fk desc) as rn
     from ${VE_BIWAREHOUSE_DB_SCHEMA}.fct_meter_event
) as x
where rn = 1;
