function format(timezone, format, time){
  var timeFormat = new java.text.SimpleDateFormat(format);
  timeFormat.setTimeZone(java.util.TimeZone.getTimeZone(timezone));
  return java.lang.Integer.parseInt(timeFormat.format(new java.util.Date(java.lang.Long.parseLong(time))));
}

var timeTK = format("UTC", "HHmm", time);
var dateTK = format("UTC", "yyyyMMdd", time);
var tz_timeTK = timeTK;
var tz_dateTK = dateTK;
if (timezone != 'N/A') {
 tz_timeTK = format(timezone, "HHmm", time);
 tz_dateTK = format(timezone, "yyyyMMdd", time);
}

var TAG = 'CASCLIENT';
var CAS_LOGIN_URL_PART = 'login';
var CAS_LOGOUT_URL_PART = 'logout';
var CAS_SERVICE_VALIDATE_URL_PART = 'serviceValidate';
var CAS_TICKET_BEGIN = 'ticket=';
var CAS_LT_BEGIN = "name=\"lt\" value=\"";
var CAS_USER_BEGIN = '<cas:user>';
var CAS_USER_END = '</cas:user>';

/**
 * Helper function to do an api call
 *
 * @param string url
 * @param object data Data to send
 * @param string method POST or GET
 * @return XMLHttpRequest
 */
function _apiRequest(url, data, method) {
	// Predefined static post data
	var requestData = {
	};
	// Merge predefined jsonData with user data
	if (data) {
		for (var i in data) {
	   			requestData[i] = data[i];
		}
	}
	var httpMethod = null;
	if (method == 'GET') {
		var dataArray = [];
		for (var i in requestData) {
			dataArray.push(i + '=' + encodeURIComponent(requestData[i]));
		}
		url += '?' + dataArray.join('&');

		httpMethod = new Packages.org.apache.commons.httpclient.methods.GetMethod(url);
		//httpMethod.addRequestHeader('Content-Type', 'application/json');
	}
	else {
		httpMethod = new Packages.org.apache.commons.httpclient.methods.PostMethod(url);
		httpMethod.addRequestHeader('Content-Type', 'application/json');
		httpMethod.setRequestBody(JSON.stringify(requestData));
	}

	var httpClient = new Packages.org.apache.commons.httpclient.HttpClient();
	httpClient.setConnectionTimeout(0);
	httpClient.setTimeout(0);
	http_status_code = httpClient.executeMethod(httpMethod);

	return httpMethod;
};

